                            </ul> */}
                        </div>
                    </div>
                    <div id='md-main-skill'>
                        <MentorShowRadar />
                    </div>
                </div>
                <hr />
                <div id='md-text' >
                    <h5>{data.text.textHeader}</h5>
                    <p>{data.text.textContent}</p>
                </div>
                <hr />
                <div id='md-galery'>
                    <ul className='flex'>
                        {data.galery.map(item => {
                            return (

                                <li className='flex'>
                                    <img src={item} />
                                </li>
                            )
                        })}
                    </ul>
                </div>
                <hr />
                <div id='md-sosial-media'>
                    <ul className='flex'>
                        {data.sm.map(item => {
                            return (
                                <li className={item.class}>
                                    <i className={item.logo}></i>
                                    <h6>{item.name}</h6>
                                </li>
                            )
                        })}

                    </ul>
                </div>
            </div>
        </div>
    )
}

export default MentorShow
