    const item = e;
    console.log(!item.check);
    console.log(e.id);
    // setFilter([
    //   ...Filter,
    //   ...item,
    //   // check : !item.c
    // ])
  };
  console.log(Filter);

  return (
    <div id="statistic">
      <div id="statistic-block">
        <div className="container">
          <div id="statistik-flex" className="flex">
            <div id="statistic-left">
              <div id="sl-container">
                <h3>Filter</h3>
                <FormGroup>
                  {Filter.map(department => {
                    console.log(dep);
                    return (
                      <label className="flex">
                        {department.map(item => {
                          return (
                            <div>
                              <h4>{item.name}</h4>
                              <FormControlLabel
                                control={
                                  <Switch size="small" checked={item.check} />
                                }
                              />
                            </div>
                          );
                        })}
                      </label>
                    );
                  })}
                </FormGroup>
              </div>
            </div>
            <div id="statistic-right">
              <div id="sr-container">
                {/* {Filter.map(item => {
                        return(
                            <item.department />
                        )
                    })} */}
              </div>
            </div>
          </div>
        </div>
      </div>
