import { MenuList } from '@material-ui/core';
import React,{useState} from 'react'
import img from "../SourceFiles/Header/male.png";
import img1 from '../SourceFiles/Mentors/img1.jpg'
import img2 from '../SourceFiles/Mentors/img2.jpg'
import img3 from '../SourceFiles/Mentors/img3.jpg'
import img4 from '../SourceFiles/Mentors/img4.jpg'
import img5 from '../SourceFiles/Mentors/img5.jpg'
import img6 from '../SourceFiles/Mentors/img6.jpg'
import img7 from '../SourceFiles/Mentors/img7.jpg'
import img8 from '../SourceFiles/Mentors/img8.jpg'
import img9 from '../SourceFiles/Mentors/img9.jpg'
import img10 from '../SourceFiles/Mentors/img10.jpg'
import img11 from '../SourceFiles/Mentors/img11.jpg'
import img12 from '../SourceFiles/Mentors/img12.jpg'
import img13 from '../SourceFiles/Mentors/img13.jpg'
import img14 from '../SourceFiles/Mentors/img14.jpg'

function Mentors() {
    const [MentorList, setMentorList] = useState([
        {
            id : 1,
            name : 'Mentor 1',
            lastname : 'soyad',
            department : 'sobe',
            skil : 'bacariqlar',
            about : 'haqqinda',
            text : {
                textHeader : 'Lorem Ipsum'
            },
            galery : [img1, img2, img3, img4, img5, img6, img7, img8],
            sm : [
                {
                    logo : 'fab fa-facebook',
                    class : 'flex fb',
                    name : 'Facebook'
                },
                {
                    logo : 'fab fa-instagram',
                    class : 'flex ins',
                    name : 'Instagram'
                },
                {
                    logo : 'fab fa-linkedin-in',
                    class : 'flex in',
                    name : 'Linkedin'
                }
            ]
        },
        {
            id : 2,
            name : 'Mentor 2',
            lastname : 'soyad',
            department : 'sobe',
            skil : 'bacariqlar',
            about : 'haqqinda',
            galery : [img9, img5, img4, img2, img11, img12, img10, img1],
            sm : [
                {
                    logo : 'fab fa-facebook',
                    class : 'flex fb',
                    name : 'Facebook'
                },
                {
                    logo : 'fab fa-instagram',
                    class : 'flex ins',
                    name : 'Instagram'
                },
                {
                    logo : 'fab fa-linkedin-in',
                    class : 'flex in',
                    name : 'Linkedin'
                }
            ]
        },
        {
            id : 3,
            name : 'Mentor 3',
            lastname : 'soyad',
            department : 'sobe',
            skil : 'bacariqlar',
            about : 'haqqinda',
            galery : [img6, img14, img2, img9, img12, img3, img13, img1],
            sm : [
                {
                    logo : 'fab fa-facebook',
                    class : 'flex fb',
                    name : 'Facebook'
                },
                {
                    logo : 'fab fa-instagram',
                    class : 'flex ins',
                    name : 'Instagram'
                },
                {
                    logo : 'fab fa-linkedin-in',
                    class : 'flex in',
                    name : 'Linkedin'
                }
            ]
        },
        {
            id : 4,
            name : 'Mentor 4',
            lastname : 'soyad',
            department : 'sobe',
            skil : 'bacariqlar',
            about : 'haqqinda',
            galery : [img2, img4, img6, img8, img10, img12, img14, img1],
            sm : [
                {
                    logo : 'fab fa-facebook',
                    class : 'flex fb',
                    name : 'Facebook'
                },
                {
                    logo : 'fab fa-instagram',
                    class : 'flex ins',
                    name : 'Instagram'
                },
                {
                    logo : 'fab fa-linkedin-in',
                    class : 'flex in',
                    name : 'Linkedin'
                }
            ]
        },

    ])
    const [MentorShow, setMentorShow] = useState(1)
    const MentorChange = (e) => {
        setMentorShow(e)
    }
    return (
        <div id = 'mentors'>
            <div id = 'mentors-block' className = 'container'>
                <div id = 'mentors-flex' className = 'flex'>
                <div id = 'mentor-list'>
                    <ul>
                        {MentorList.map(item => {
                            return(
                                <li className = 'mentor-list-item flex' onClick = {()=> {
                                    MentorChange(item.id)
                                }}>
                                <img  src = {img}/>
                                <h5>{item.name}</h5>
                            </li>
                            )
                        })}
                    </ul>
                </div>
                <div id = 'mentor-data'>
                    <div id = 'md-header'>
                        <div id = 'md-header-flex' className = 'flex'>
                            <hr id = 'md-header-hr-left'/>
                            <img src = {img}/> 
                            <h3>Name</h3>
                            <hr id = 'md-header-hr-right'/>
                        </div>
                    </div>
                    <div id = 'md-content'>
                        <div></div>
                        <div id = 'md-main' className = 'flex'>
                            <div id = 'md-main-conten'></div>
                            <div id = 'md-main-skill'></div>
                        </div>
                        <hr/>
                        <div id = 'md-text' >
                            <h5>Lorem Ipsum</h5>
                            <p>Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>
                        </div>
                        <hr/>
                        <div id = 'md-galery'>
                                {MentorList.map(item => {
                                    if(item.id == MentorShow){
                                        return(
                                        <ul className = 'flex'>
                                            {item.galery.map(image => {
                                                return(
                                                    <li className = 'flex'>
                                                        <img src = {image} />
                                                    </li>
                                                )
                                            })}
                                        </ul>
                                    )
                                    }
                                })}
                        </div>
                        <hr />
                        <div id = 'md-sosial-media'>
                            {
                                MentorList.map(mentor => {
                                    if(mentor.id == MentorShow){
                                        return(
                                            <ul className = 'flex'>
                                                {mentor.sm.map(item => {
                                                    return(
                                                        <li className = {item.class}>
                                                            <i className = {item.logo}></i>
                                                            <h6>{item.name}</h6>
                                                        </li>
                                                    )
                                                })}
                                            </ul>
                                        )
                                    }

                                })
                            }
                        </div>
                    </div>
                </div>
                </div>
            </div>
        </div>
    )
}

export default Mentors
