import React, { useState } from "react";
import Logo from "./SourceFiles/Header/Logo.png";
import { Button, TextField } from "@material-ui/core/";


function App() {
  const [Manu, setManu] = useState({
    item: ["Haqq覺m覺zda", "Mentorlar"],
    Account: {
      class: "fas fa-user",
      text: "",
      display : 'display-none'
    },
  });

  const [User, setUser] = useState([
      {
          login : 'admin',
          pw : 12345 
      }
  ])

  const [UserClick, setUserClick] = useState(true);

  const userClicked = () => {
    setUserClick((prevState) => !prevState);
    console.log(UserClick)
    if(UserClick){
        setManu({
            ...Manu, 
            Account : {
                ...Manu.Account,
                display : 'flex'
            }
        })
    }else{
        setManu({
            ...Manu, 
            Account : {
                ...Manu.Account,
                display : 'display-none'
            }
        })
    }
  };

  const SignIn = (e) => {

  }

  return (
    <div className="App">
      <div id="header" >
      <div id="header-block">
        <div className="container">
          <div id="flex-container" className="flex">
            <div id="header-logo">
              <img src={Logo} />
            </div>
            <div id="header-manu">
              <nav>
                <ul className="flex">
                  {Manu.item.map((item) => {
                    return <li>{item}</li>;
                  })}
                  <li className={Manu.Account.class} onClick={userClicked}>
                    {Manu.Account.text}
                  </li>
                </ul>
              </nav>
            </div>
          </div>
        </div>
      </div>
      <div id="acc-block" className = {Manu.Account.display}>
        <div id="acc-container">
          <h4>VTP 21</h4>
          <form>
            <TextField
              id="outlined-basic"
              label="Login"
              variant="outlined"
              className="acc-input"
            />
            <TextField
              id="outlined-basic"
              label="Parol"
              variant="outlined"
              className="acc-input"
            />
            <Button
              variant="contained"
              color="primary"
              className="acc-input acc-input-button"
            >
              Daxil ol
            </Button>
          </form>
        </div>
      </div>
    </div>
    </div>
  )
}

export default App;
