import React,{useState} from "react";
import { Switch, FormGroup, FormControlLabel } from "@material-ui/core";
import Activity from '../Component/Charts/Activity'
import Age from '../Component/Charts/Age'
import Degree from '../Component/Charts/Degree'
import Departments from '../Component/Charts/Departments'
import Gender from '../Component/Charts/Gender'
import Speciality from '../Component/Charts/Speciality'
import University from '../Component/Charts/University'

function Statistic() {
    const [Filter, setFilter] = useState([
        {
          id : 1,
            check : true,
            name : 'Cins',
            department : Gender
        },
        {
          id : 2,
            check : true,
            name : 'Şöbə',
            department : Departments
        },
        {
          id : 2,
            check : true,
            name : 'Aktivlik',
            department : Activity
        },
        {
            check : true,
            name : 'Yaş',
            department : Age
        },
        {
            check : true,
            name : 'Universitet',
            department : University
        },
        {
            check : true,
            name : 'İxtisas',
            department : Speciality
        },
        {
            check : true,
            name : 'Kusr',
            department : Degree
        }

    ])

    const StatisticFilter = (e) => {
      setFilter([{
        ...Filter, 
        check : !e
      }])
    }

  return (
    <div id="statistic">
      <div id="statistic-block">
        <div className="container">
          <div id="statistik-flex" className="flex">
            <div id="statistic-left">
              <div id="sl-container">
                <h3>Filter</h3>
                <FormGroup>
                    {Filter.map(item => {
                        return(
                            <label className = 'flex'>
                            <h4>{item.name}</h4>
                            <FormControlLabel
                              control={
                                <Switch
                                  size="small"
                                  checked = {item.check}
                                  onClick = {() => {
                                    StatisticFilter(item.check)
                                  }}
                                />
                              }
                            />
                            </label>
                        )
                    })}
                </FormGroup>
              </div>
            </div>
            <div id="statistic-right">
                <div id = 'sr-container'>
                    {Filter.map(item => {
                        return(
                            <item.department />
                        )
                    })}
                </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Statistic;
