import React, { useState } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

import Logo from "./SourceFiles/Header/Logo.png";
import { Button, Menu, TextField } from "@material-ui/core/";

import Statistic from './Section/Statistic';
import Members from "./Section/Members";
import Account from "./Section/Account";
import Home from "./Section/Home";
import Mentors from "./Section/Mentors";

function App() {
  const [Manu, setManu] = useState({
    link: [
      {
        link: "/",
        name: "Haqqımızda",
      },
      {
        link: "/statistic",
        name: "Statistika",
      },
      {
        link: "/mentors",
        name: "Mentorlar",
      },
      {
        link: "/members",
        name: "İştirakçılar",
      },
      {
        link: "/plan",
        name: "Plan",
      },
    ],
    Account: {
      class: "fas fa-user",
      text: "",
      display: "display-none",
    },
  });

  const [User, setUser] = useState([
    {
      login: "admin",
      pw: 12345,
    },
  ]);

  const [UserClick, setUserClick] = useState(true);

  const userClicked = () => {
    setUserClick((prevState) => !prevState);
    console.log(UserClick);
    if (UserClick) {
      setManu({
        ...Manu,
        Account: {
          ...Manu.Account,
          display: "flex",
        },
      });
    } else {
      setManu({
        ...Manu,
        Account: {
          ...Manu.Account,
          display: "display-none",
        },
      });
    }
  };

  const SignIn = (e) => {};

  return (
    <Router>
      <div className="App">
        <div id="header">
          <div id="header-block">
            <div className="container">
              <div id="header-flex" className="flex">
                <div id="header-logo">
                  <img src={Logo} />
                </div>
                <div id="header-manu">
                  <nav>
                    <ul className="flex">
                      {Manu.link.map((item) => {
                        return(
                          <li>
                            <Link to = {item.link}>
                            <h3>{item.name}</h3>
                            </Link>
                          </li>
                        )
                      })}
                      <li className={Manu.Account.class} onClick={userClicked}>
                        <Link>
                         <h3>{Manu.Account.text}</h3>
                        </Link>
                      </li>
                    </ul>
                  </nav>
                </div>
              </div>
            </div>
          </div>
          <div id="acc-block" className={Manu.Account.display}>
            <div id="acc-container">
              <h4>VTP 21</h4>
              <form>
                <TextField
                  id="outlined-basic"
                  label="Login"
                  variant="outlined"
                  className="acc-input"
                />
                <TextField
                  id="outlined-basic"
                  label="Parol"
                  variant="outlined"
                  className="acc-input"
                />
                <Button
                  variant="contained"
                  color="primary"
                  className="acc-input acc-input-button"
                >
                  Daxil ol
                </Button>
              </form>
            </div>
          </div>
        </div>
        <Switch>
          <Route path="/" exact component = {Home} />
          <Route path="/statistic" component = {Statistic} />
          <Route path="/mentors"  component = {Mentors} />
          <Route path="/members"  component = {Account} />
          <Route path="/account"  component = {Account} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
